import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './report.css';

const ReportPage = () => {
    const [recommendations, setRecommendations] = useState([]);

    useEffect(() => {
        // Fetch recommendation data from Flask backend
        axios.get('/get_recommendations')
            .then(response => {
                setRecommendations(response.data);
            })
            .catch(error => {
                console.error('Error fetching recommendations:', error);
            });
    }, []);

    return (
        <div className="report-page">
            <h2 className="heading">Recommendation Report</h2>
            <div className="image-container">
                <img src="/med1.jpg" alt="Medicine 1" />
                <img src="/med2.jpg" alt="Medicine 2" />
            </div>
            <div className="recommendations-box">
                <h3>Recommended Medications:</h3>
                <ul>
                    {recommendations.map((medication, index) => (
                        <li key={index}>{medication}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ReportPage;
