import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Report = () => {
    const [recommendations, setRecommendations] = useState([]);

    useEffect(() => {
        axios.get('/recommendations', {
            params: {
                insulin: 1,
                metformin: 1,
                glipizide: 1,
                glyburide: 1,
                pioglitazone: 1,
                rosiglitazone: 1,
                glimepiride: 1,
                repaglinide: 1,
                'glyburide-metformin': 1,
                nateglinide: 1,
                patientName: '', // You can add more fields if required
                age: '', // Add more fields here if needed
                weight: '',
                patientNbr: '',
                gender: '',
                race: ''
            }
        })
        .then(response => {
            setRecommendations(response.data.recommendations); // Assuming recommendations are sent in the response under 'recommendations' key
        })
        .catch(error => {
            console.error('Error fetching recommendations:', error);
        });
    }, []);

    return (
        <div className="report-page">
            <h2 className="heading">Recommendation Report</h2>
            <div className="recommendations-box">
                <h3>Recommended Medications:</h3>
                <ul>
                    {recommendations && Array.isArray(recommendations) ? (
                        recommendations.map((medication, index) => (
                            <li key={index}>{medication}</li>
                        ))
                    ) : (
                        <li>No recommendations available</li>
                    )}
                </ul>
            </div>
        </div>
    );
};

export default Report;
