import React, { useEffect, useState } from "react"
import axios from "axios"
import {useNavigate, Link} from "react-router-dom"
import './SignIn.css'
import ImageInput from "./ImageInput"
import user_icon from './images/user.jpg';
import email_icon from './images/email.jpg';
import password_icon from './images/padlock.jpg';

function SignIn() {
    const history=useNavigate();

    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')

    async function submit(e){
        e.preventDefault();

        try{

            await axios.post("http://localhost:8000/",{
                email,password
            })
            .then(res=>{
                if(res.data=="exist"){
                    history("/home",{state:{id:email}})
                }
                else if(res.data=="notexist"){
                    alert("User have not sign up")
                }
            })
            .catch(e=>{
                alert("wrong details")
                console.log(e);
            })

        }
        catch(e){
            console.log(e);

        }

    }


    return (
        <div className="login_container">
            <div className="container">
                <div className="login">
                    <h1>Login</h1>
                    <form action="POST">
                        <ImageInput type="email" placeholder="Email" icon={email_icon} onChange={(e) => { setEmail(e.target.value) }} />
                        <ImageInput type="password" placeholder="Password" icon={password_icon} onChange={(e) => { setPassword(e.target.value) }} /> 
                        <input type="submit" onClick={submit} />
                    </form>
                    <br />
                    <p>OR</p>
                    <br />

                    <Link to="/sign">Signup Page</Link>

                </div>
            </div>
        </div>
        
    )
}

export default SignIn;
