import React, { useEffect, useState } from "react"
import axios from "axios"
import { useNavigate, Link } from "react-router-dom"

import ImageInput from "./ImageInput"
import user_icon from './images/user.jpg';
import email_icon from './images/email.jpg';
import password_icon from './images/padlock.jpg';


function Login() {
    const history=useNavigate();

    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const [name, setName]= useState('')

    async function submit(e){
        e.preventDefault();

        try{

            await axios.post("http://localhost:8000/signup",{
                email,password
            })
            .then(res=>{
                if(res.data=="exist"){
                    alert("User already exists")
                }
                else if(res.data=="notexist"){
                    history("/home",{state:{id:email}})
                }
            })
            .catch(e=>{
                alert("wrong details")
                console.log(e);
            })

        }
        catch(e){
            console.log(e);

        }

    }


    return (
        <div className="login">

            <h1>Signup</h1>

            <form action="POST">
                <ImageInput type="name" placeholder="Name" icon={user_icon} onChange={(e) => { setName(e.target.value) }} />
                <ImageInput type="email" placeholder="Email" icon={email_icon} onChange={(e) => { setEmail(e.target.value) }} />
                <ImageInput type="password" placeholder="Password" icon={password_icon} onChange={(e) => { setPassword(e.target.value) }} /> 
                <input type="submit" onClick={submit} />
            </form>

            <br />
            <p>OR</p>
            <br />

            <Link to="/sign-up">Login Page</Link>

        </div>
    )
}

export default Login
