import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Report = () => {
    const [recommendations, setRecommendations] = useState([]);

    useEffect(() => {
        // Fetch recommendation data from Flask backend
        axios.get('http://localhost:5000/recommendations', {
            params: {
                insulin: 1,
                metformin: 1,
                glipizide: 1,
                glyburide: 1,
                pioglitazone: 1,
                rosiglitazone: 1,
                glimepiride: 1,
                repaglinide: 1,
                'glyburide-metformin': 1,
                nateglinide: 1,
            }
        })
        .then(response => {
            setRecommendations(response.data.recommended_drugs);
        })
        .catch(error => {
            console.error('Error fetching recommendations:', error);
        });
    }, []);

    return (
        <div className="report-page">
            <h2 className="heading">Recommendation Report</h2>
            <div className="recommendations-box">
                <h3>Recommended Medications:</h3>
                <ul>
                    {recommendations.map((medication, index) => (
                        <li key={index}>{medication}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Report;
