import React, { useEffect, useState } from "react"
import axios from "axios"
import {useNavigate, Link} from "react-router-dom"
import './SignIn.css'
import user_icon from './images/user.jpg';
import email_icon from './images/email.jpg';
import password_icon from './images/padlock.jpg';

function SignIn() {
    const history=useNavigate();

    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')

    async function submit(e){
        e.preventDefault();

        try{

            await axios.post("http://localhost:8000/",{
                email,password
            })
            .then(res=>{
                if(res.data=="exist"){
                    history("/home",{state:{id:email}})
                }
                else if(res.data=="notexist"){
                    alert("User have not sign up")
                }
            })
            .catch(e=>{
                alert("wrong details")
                console.log(e);
            })

        }
        catch(e){
            console.log(e);

        }

    }


    return (
        <div className="container">
          <div className="login">
            <h1>Login</h1>
            <form action="POST">
                <div className="input-container">
                    <input type="email" onChange={(e) => { setEmail(e.target.value) }} placeholder="Email" style={{ backgroundImage: `url(./images/user.jpg)` }} />
                </div>
                <div className="input-container">
                    <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="Password" style={{ backgroundImage: `url(./images/user.jpg)` }} />
                </div>
                <input type="submit" onClick={submit} />
            </form>
            <br />
            <p>OR</p>
            <br />
            <Link to="/signup">Signup Page</Link>
          </div>
        </div>
      );
}

export default SignIn;
