import React, { useState } from 'react';
import './Login.css';

import user_icon from './images/user.jpg';
import email_icon from './images/email.jpg';
import password_icon from './images/padlock.jpg';
import loginBg from './images/LoginBack.jpg';

const Login = () => {
    const [action, setAction] = useState("Sign Up");
    const handleSubmit = () => {
        // Handle form submission and navigation to the next page
        console.log("Form submitted!");
    };
    return (
            <div className='container'>
            <div className="header">
                <div className="text">{action}</div>
                <div className='Underline'></div>
            </div>
            <div className='inputs'>
                {action === "Login" ? null : (
                    <div className="input">
                        <img src={user_icon} alt=""/>
                        <input type="text" placeholder="Name"/>
                    </div>
                )}
                <div className="input">
                    <img src={email_icon} alt=""/>
                    <input type="email" placeholder="Email ID"/>
                </div>
                <div className="input">
                    <img src={password_icon} alt=""/>
                    <input type="password" placeholder="Password"/>
                </div>
            </div>
            {action === "Sign Up" ? null : (
                <div className="forgot-pass">Forgot password? <span>Click here!</span></div>
            )}
            <div className="submit-container">
                <div className={action === "Login" ? "submit active" : "submit"} onClick={() => setAction("Login")}>Login</div>
                <div className={action === "Sign Up" ? "submit active" : "submit"} onClick={() => setAction("Sign Up")}>Sign up</div>
                
            </div>
                <div className="button">
                <button className='submit-button' onClick={handleSubmit}>Submit</button>
                </div>
        </div>
        
    );
};

export default Login;
